<?phpclass Core_Config{    private static $a_config = array();    private static $isInitialized = false;    private static function init()    {        if(!static::$isInitialized) {            echo $_SERVER['HTTP_HOST'];            $baseDir = realpath(__DIR__ . '/../../');            $baseUrl = '/';            $a_vals = array(                'server_path' => $baseDir,                'images_path' => $baseDir . 'images/',                'libs_path' => $baseDir . 'libs/',                'application_path' => $baseDir . 'application/',                'cms_dir' => $baseUrl . 'cms',                'full_cms_path' => $baseUrl . 'www/cms',                'page_dir' => $baseUrl,                'www_url' => 'http://' . $_SERVER['HTTP_HOST'] . '/'            );            static::$a_config = array_merge(static::$a_config, $a_vals);            static::$isInitialized = true;        }    }    public static function get($s_name)    {        static::init();        return static::$a_config[$s_name];    }    public static function set($s_name, $val)    {        static::$a_config[$s_name] = $val;    }    public static function loadIni($s_path)    {        static::init();        $a_loadedConfig = parse_ini_file($s_path);        static::$a_config = array_merge(static::$a_config, $a_loadedConfig);    }    public function printr()    {        return "Core Config<pre>" . print_r(static::$a_config, true) . "</pre>";    }}