<?phpclass Core_Config{    private static $a_config = array();    //=====================================================    public static function get($s_name)    {        return self::$a_config[$s_name];    }    //=====================================================    public static function set($s_name, $val)    {        self::$a_config[$s_name] = $val;    }    //=====================================================    public static function loadIni($s_path)    {        $a_loadedConfig = parse_ini_file($s_path);        self::$a_config = array_merge_recursive(self::$a_config, $a_loadedConfig);    }    //=====================================================    public function printr()    {        return "Core Config<pre>" . print_r(self::$a_config, true) . "</pre>";    }}