=================================================	public function getRefererRoute()	{		return $this->refererRoute;	}	public function setRefererRoute($refererRoute_in)	{		$this->refererRoute = $refererRoute_in;	}	//================================================================================================================	public function getModul()	{		return $this->modul;	}	public function setModul($modul_in)	{		$this->modul = $modul_in;	}	//================================================================================================================	public function getAkcja()	{		return $this->akcja;	}	public function setAkcja($akcja_in)	{		$this->akcja = $akcja_in;	}	//================================================================================================================	public function getParametr($s_parametr)	{		if(isset($this->parametry[$s_parametr])) {			return $this->parametry[$s_parametr];		}		return null;	}	public function getParametry()	{		return $this->parametry;	}	public function setParametry($parametry_in)	{		$this->parametry = $parametry_in;	}	public function setParametr($parametr_nazwa, $parametr_wartosc)	{		$this->parametry[$parametr_nazwa] = $parametr_wartosc;	}	//================================================================================================================	public function getParametrGet($s_parametr)	{		if(isset($this->parametryGet[$s_parametr])) {			return $this->parametryGet[$s_parametr];		}		return null;	}	public function getParametryGet()	{		return $this->parametryGet;	}	public function setParametryGet($parametry_in)	{		$this->parametryGet = $parametry_in;	}	public function setParametrGet($parametr_nazwa, $parametr_wartosc)	{		$this->parametryGet[$parametr_nazwa] = $parametr_wartosc;	}	//================================================================================================================	public function getPliki()	{		return $this->pliki;	}	public function setPliki($plik_in)	{		$this->pliki = $plik_in;	}	//================================================================================================================	public function getCiasteczka()	{		return $this->ciasteczka;	}	public function setCiasteczka($ciasteczka_in)	{		$this->ciasteczka = $ciasteczka_in;	}}