<?phpclass Model_Jezyk{    const DEFAULT_ID = 1;	public $id = '';	public $nazwa = '';	public $miejsce = '';	public $skrot = '';	public $aktywny = '';	public $errors = array();	public function __construct($id = '')	{		if ((int)$id > 0) {			$this->pobierzSzczegoly($id);		}	}	public function pobierzPrzezSkrot($skrot)	{		$db = Core_DB::instancja();		if (trim($skrot) != '') {			$sql    = 'SELECT jezyk_id AS id FROM jezyki WHERE jezyk_skrot = "' . trim($skrot) . '"';			$result = $db->get_row($sql);			if (count($result) > 0) {				$this->pobierzSzczegoly($result['id']);			} else {				$this->errors[] = 'Nie odnaleziono języka o skrócie: ' . trim($skrot);			}		}	}	public function pobierzSzczegoly($id)	{		$db = Core_DB::instancja();		if ((int)$id > 0) {			$sql = 'SELECT * FROM jezyki WHERE jezyk_id = ' . (int)$id . ' LIMIT 1';			$res = $db->get_row($sql);			if (count($res) > 0) {				$this->id      = $res['jezyk_id'];				$this->nazwa   = $res['jezyk_nazwa'];				$this->miejsce = $res['jezyk_miejsce'];				$this->skrot   = $res['jezyk_skrot'];				$this->aktywny = $res['jezyk_aktywny'];			} else {				$this->errors[] = 'Nie odnaleziono rekordu o nr id: ' . $id . '.';			}		}		if (count($this->errors) > 0) {			return false;		} else {			return true;		}	}	public static function pobierzWszystkie()	{		$db = Core_DB::instancja();		$res = $db->Execute('SELECT jezyk_id AS id, jezyk_skrot AS nazwa FROM jezyki WHERE jezyk_aktywny=1');		$a_jezyki = array();		if (count($res) > 0) {			foreach ($res as $jezyk) {				$a_jezyki[$jezyk['id']] = $jezyk['nazwa'];			}		}		return $a_jezyki;	}}